        <div *ngIf="showProfile; else recipeOrAdd">
          <app-user-profile></app-user-profile>
        </div>
        <ng-template #recipeOrAdd>
          <div *ngIf="showRecipe; else addRecipeSection">
            <section class="section food-menu" id="food-menu">
              <div class="container">
                <h2 class="h2 section-title">
                  My <span class="span">Recipes</span>
                </h2>
                <ul class="food-menu-list" >

                  <li *ngFor="let recette of recettes">
                    <div class="food-menu-card">

                      <div class="card-banner">
                        <ng-container *ngIf="recette.medias && recette.medias.length > 0">
                        <img src="{{recette.medias[0].url}}" width="300" height="300" loading="lazy"
                          alt="Fried Chicken Unlimited" class="w-100">
                          </ng-container>

                        <!-- <div class="badge">-15%</div> -->

                        <button class="btn food-menu-btn" (click)="viewRecette(recette.id)">View details</button>
                      </div>

                      <div class="wrapper">
                        <p class="category">{{recette.utilisateurCreateur?.user_name}}</p>

                        <div class="rating-wrapper">
                          <button  class="button-10" *ngIf="recette.id !== undefined" (click)="deleteRecette(recette.id)">Delete</button>
                          <button  class="button-10" *ngIf="recette.id !== undefined" (click)="updateRecette(recette.id)">Update</button>

                        </div>
                      </div>

                      <h3 class="h3 card-title">{{ recette.nom }}</h3>

                      <div class="price-wrapper">

                        <p class="price-text">Durée Préparation:</p>

                        <data class="price">{{recette.dureePreparation}}</data>

                      </div>

                    </div>
                  </li>


                </ul>


              </div>
            </section>




          </div>
          <ng-template #addRecipeSection>
            <div *ngIf="showAddRecipe; else ShowUsers">
