import { PersonneService } from './../services/personne.service';
import { Component} from '@angular/core';
import { ActivatedRoute,Router } from '@angular/router';
import { Location } from '@angular/common';
import { RecetteService } from '../services/recette.service';
import { Personne } from '../models/personne';
@Component({
  selector: 'app-user-info',
  templateUrl: './user-info.component.html',
  styleUrls: ['./user-info.component.css']
})
export class UserInfoComponent {
  constructor(
    private router: Router,private PersonneService:PersonneService,
    private location: Location,private recetteService: RecetteService,private personService:PersonneService,
    private route: ActivatedRoute) { }

  ngOnInit(): void {
    this.id = Number(this.route.snapshot.paramMap.get('id'));
    this.fetchPersonne();
    }
    personne:Personne={};
    id!:number;

  fetchPersonne(): void {

    this.personService.showOnePerson(25).subscribe(
      (data: any) => {
        this.personne = data;
        console.log(this.personne);
    },
    error => {
      console.log('Une erreur s\'est produite lors du chargement des catégories : ', error);
    }
   );
  }

}
<div class="card">
  <div class="card_background_img"></div>
  <div class="card_profile_img"></div>
  <div class="user_details">
      <h3>{{personne.nomComplet}}</h3>
      <!-- <p>Master Chef</p> -->
  </div>
  <div class="card_count">
      <div class="count">
          <div class="fans">
              <h3>2.4M</h3>
              <p>Followers</p>
          </div>
          <div class="following">
              <h3>202</h3>
              <p>Followings</p>
          </div>
          <div class="post">
              <h3>552</h3>
              <p>recipes</p>
          </div>
      </div>
       <div ><button class="btn btn-hover">Follow</button></div>
  </div>
</div>
