import { IngredientService } from './../services/ingredient.service';
import { Component } from '@angular/core';
import {ActivatedRoute, Router } from '@angular/router';
import { Ingredient } from '../models/ingredient';

@Component({
  selector: 'app-add-ingredient',
  templateUrl: './add-ingredient.component.html',
  styleUrls: ['./add-ingredient.component.css']
})
export class AddIngredientComponent {
  name: string='';
  description: string='';
  itemImage: string='';
  ingredient: Ingredient = {} ;
  isUpdateMode: boolean = false; // Variable to track the mode (add or update)
  id!:number;


  constructor(private IngredientService: IngredientService,private route: ActivatedRoute, private router: Router) {}
  ngOnInit(): void {
    // Check if there's an ID in the URL to determine the mode
    this.id = Number(this.route.snapshot.paramMap.get('id'));
    this.isUpdateMode = !!this.id; // Convert the ID to a boolean (true if ID exists, false if not)
    console.log(this.id)
    this.getIngredient();
  }

  extractFileName(event: any): void {
    const files = event.target.files;
    if (files && files.length > 0) {
      this.itemImage = files[0].name;
    }
  }
  onSubmit(): void {
    if (this.isUpdateMode ) {
      // Perform update logic here
      this.updateIngredient();
    } else {
      // Perform add logic here
      this.AddIngredient();

    }
  }
  AddIngredient():void{
    const Ingredient: Ingredient = {
      nom: this.ingredient.nom,
      description: this.ingredient.description,
      media: this.itemImage
    };
    this.IngredientService.createIngredient(Ingredient).subscribe(
      () => {
        console.log('Catégorie ajoutée avec succès');
        this.router.navigate(['ingredients']);

      },
      error => {
        console.error('Erreur lors de l\'ajout de la catégorie', error);
      }
    );
  }
  updateIngredient(): void {
    const Ingredient: Ingredient = {
      nom: this.ingredient.nom,
      description: this.ingredient.description,
      media: this.itemImage
    };
    this.IngredientService.updateIngredient(this.id,Ingredient).subscribe(
      () => {
        console.log('category updated successfully.');
        this.router.navigate(['ingredients']);
        console.log(Ingredient)

      },
      error => {
        console.log(error);
      }

    );
  }
  getIngredient(): void {
    this.IngredientService.getIngredientById(this.id).subscribe(
      (data: any) => {
        this.ingredient = data;
    },
    error => {
      console.log('Une erreur s\'est produite lors du chargement des catégories : ', error);
    }
   );

  }
}
<section class="section section-divider white promo">
  <div class="container">
    <h2 class="h2 section-title">
      <span class="span">{{
        isUpdateMode ? "Update Ingredient" : "Add Ingredient"
      }}</span>
    </h2>
    <form (submit)="onSubmit()">
      <div class="row">
        <div class="col-25">
          <label for="fname">Name</label>
        </div>
        <div class="col-75">
          <input
            type="text"
            id="fname"
            name="firstname"
            placeholder="Name.."
            [(ngModel)]="ingredient.nom"
            ngModel
            required
            #nomInput="ngModel"
          />
        </div>
        <div class="col-25"></div>
        <div
          class="alert alert-danger col-75"
          *ngIf="nomInput.touched && !nomInput.valid"
        >
          Name is required
        </div>
      </div>
      <div class="row">
        <div class="col-25">
          <label for="subject">Description</label>
        </div>
        <div class="col-75">
          <textarea
            id="subject"
            name="subject"
            placeholder="description..."
            style="height: 200px"
            [(ngModel)]="ingredient.description"
            ngModel
            required
            #descr="ngModel"
          ></textarea>
        </div>
        <div class="col-25"></div>
        <div
          class="alert alert-danger col-75"
          *ngIf="descr.touched && !descr.valid"
        >
          Descrition is required
        </div>
      </div>
      <div class="row">
        <div class="col-25">
          <label for="subject">Image</label>
        </div>
        <div class="col-75">
          <input
            type="file"
            name="myImage"
            (change)="extractFileName($event)"
            accept="image/png, image/gif, image/jpeg, image.jpg"
            [(ngModel)]="ingredient.media"
            ngModel
            required
            #image="ngModel"
          />
        </div>
        <div class="col-25"></div>
        <div
          class="alert alert-danger col-75"
          *ngIf="image.touched && !image.valid"
        >
          Image is required
        </div>
      </div>
      <br />
      <div class="row">
        <button
          class="btn btn-hover"
          type="submit"
          style="margin-inline: auto; width: 50%"
        >
          {{ isUpdateMode ? "Update Ingredient" : "Add Ingredient" }}
        </button>
      </div>
    </form>
  </div>
</section>
