    </div>

    <div class="right">
      <form (ngSubmit)="Login(user)">
      <h2 class="h2 section-title"><span class="span">Login</span></h2>
      <p>
        Don't have an account? <a [routerLink]="['/signup']">Creat Your Account</a> it takes less
        than a minute
      </p>
      <div class="inputs">
        <input type="text" placeholder="user name"   [(ngModel)]="user.adresseMail"/>
        <br />
        <input type="password" placeholder="password"  [(ngModel)]="user.motDePasse" />
      </div>

      <br /><br />



      <br />
      <input type="submit" class="btn btn-hover" value="Sign in ">

      </form>
    </div>
  </div>

