 none;\n  background-color: #f7f7f7;\n  border: 1px solid #ccc;\n  padding: 12px;\n  font-size: 16px;\n  color: #333;\n  width: 250px;\n  border-radius: 4px;\n  outline: none;\n  transition: border-color 0.3s, background-color 0.3s;\n}\n\n.custom-select[_ngcontent-%COMP%]:hover, .custom-select[_ngcontent-%COMP%]:focus {\n  border-color: #3498db;\n  background-color: #fff;\n  box-shadow: 0 2px 5px rgba(0, 0, 0, 0.1);\n}\n\n.custom-select[_ngcontent-%COMP%]   option[_ngcontent-%COMP%] {\n  padding: 10px;\n  background-color: #f7f7f7;\n  color: #333;\n  font-size: 14px;\n  transition: background-color 0.3s, color 0.3s;\n}\n\n.custom-select[_ngcontent-%COMP%]   option[_ngcontent-%COMP%]:hover {\n  background-color: #3498db;\n  color: #fff;\n}\n\n\n\n.custom-select[_ngcontent-%COMP%]::after {\n  content: '\\f078';\n  font-family: 'Font Awesome 5 Free';\n  position: absolute;\n  top: 50%;\n  right: 15px;\n  transform: translateY(-50%);\n  color: #666;\n  pointer-events: none;\n}\n\n\n\n.custom-select[_ngcontent-%COMP%]:active::after {\n  color: #3498db;\n}\n\n\n.searchable-select[_ngcontent-%COMP%] {\n  position: relative;\n  display: inline-block;\n}\n\n.search-input[_ngcontent-%COMP%] {\n  padding: 10px;\n  border: 1px solid #ccc;\n  border-radius: 4px;\n  width: 250px;\n}\n\n\n.threeDiv.ng-pristine.ng-invalid.ng-touched.ng-dirty[_ngcontent-%COMP%]{\n  border: 2px solid red;\n}\n.threeDiv.ng-pristine.ng-invalid.ng-touched[_ngcontent-%COMP%]{\n  border: 2px solid red;\n}\n.threeDiv.ng-touched.ng-dirty.ng-invalid[_ngcontent-%COMP%]{\n  border: 2px solid red;\n}\n\n\n.alert-message[_ngcontent-%COMP%] {\n  color: red;\n  font-weight: bold;\n  margin-top: 10px;\n}\n\n/*# sourceMappingURL=data:application/json;charset=utf-8;base64, */"]
