import { MotCleService } from './../services/mot-cle.service';
import { MotCle } from './../models/mot-cle';
import { Categorie } from './../models/categorie';
import { Quantite } from './../models/quantite';
import { Ingredient } from './../models/ingredient';
import { Personne } from './../models/personne';
import { Media } from './../models/media';
import { Etape } from './../models/etape';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { RecetteService } from '../services/recette.service';
import { Recette } from '../models/recette';
import { CategorieService } from '../services/categorie.service';
import { IngredientService } from '../services/ingredient.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-add-recette',
  templateUrl: './add-recette.component.html',
  styleUrls: ['./add-recette.component.css'],
})
export class AddRecetteComponent implements OnInit {
  myForm!: FormGroup;
  // Properties to hold form data
  recette: any; // Define the recette object to hold the updated values
  id!: number;

  nom?: string = '';
  description?: string = '';
  origine?: string = '';
  dureePreparation?: number;
  dureeCuisson?: number;
  nbrPersonnes?: number;
  utilisateurCreateur?: Personne = { id: 9 };
  quantites: Quantite[] = [];
  ingredients: Ingredient[] = [];
  categories: Categorie[] = [];
  motCles: MotCle[] = [];
  etapes: Etape[] = [];
  medias: Media[] = [];

  categoriess: any;
  ingrediantss: any;
  motCless: any;

  //Variables of validation
  errorEtape: string[] = [];
  errorQuantiteIngredient: string = '';
  errorMedia: string[] = [];

  messageMedia: string[] = [];
  messageEtape: string[] = [];

  errorMessages: { [key: string]: string } = {
    nom: '',
    description: '',
    origine: '',
    dureePreparation: '',
    dureeCuisson: '',
    nbrPersonnes: '',
    utilisateurCreateur: '',
    ingredients: '',
    quantites: '',
    etapes: '',
    medias: '',
  };

  constructor(
    private recetteService: RecetteService,
    private router: Router,
    private route: ActivatedRoute,
    private ingredient: IngredientService,
    private categorie: CategorieService,
    private motCle: MotCleService,
    private formBuilder: FormBuilder
  ) {}

  ngOnInit(): void {
    this.id = Number(this.route.snapshot.paramMap.get('id'));
    if (this.id != undefined)
      this.getRecette();
    this.getCategories();
    this.getIngredient();
    this.getMotCles();

    this.myForm = this.formBuilder.group({
      nom: ['', Validators.required], // Apply required validator to ensure the field is not empty
      // description: ['', Validators.required],
      // dureePreparation: [0, Validators.required], // Apply min validator to ensure the number is non-negative
      // nbrPersonnes: [1, Validators.min(1)], // Apply min validator to ensure the number is at least 1
      // medias: this.formBuilder.array([]),
    });
  }
  getRecette(): void {
    if (this.id != undefined) {
      this.recetteService.getRecetteById(this.id).subscribe(
        (data) => {
          this.recette = data;
          this.nom = this.recette.nom;
          this.description = this.recette.description;
          this.origine = this.recette.origine;
          this.dureeCuisson = this.recette.dureeCuisson;
          this.dureePreparation = this.recette.dureePreparation;
          this.nbrPersonnes = this.recette.nbrPersonnes;
          this.utilisateurCreateur = this.recette.utilisateurCreateur;
          this.quantites = this.recette.quantites || [];
          this.ingredients = this.recette.ingredients || [];
          this.categories = this.recette.categories || [];
          this.motCles = this.recette.motCles || [];
          this.etapes = this.recette.etapes || [];
          this.medias = this.recette.medias || [];
        },
        (error) => {
          console.log(error);
        }
      );
    }
  }
  // show Categories
  getCategories() {
    this.categorie.getCategories().subscribe(
      (data2) => {
        console.log(data2);
        this.categoriess = data2;
      },
      (error) => {
        console.log(error);
      }
    );
  }
  //show ingrediants
  getIngredient() {
    this.ingredient.getIngredients().subscribe(
      (data1) => {
        console.log(data1);
        this.ingrediantss = data1;
      },
      (error) => {
        console.log(error);
      }
    );
  }
  //show mot cles
  getMotCles() {
    this.motCle.getMotCles().subscribe(
      (data1) => {
        console.log(data1);
        this.motCless = data1;
      },
      (error) => {
        console.log(error);
      }
    );
  }

  // Method to handle form submission
  createRecette(): void {
    this.ingredients = this.ingredients.filter(
      (element) => element !== undefined
    );
    this.quantites = this.quantites.filter((element) => element !== undefined);
    this.etapes = this.etapes.filter((element) => element !== undefined);
    const formData = new FormData();
    this.medias.forEach((media) => {
      if (media.file) {
        formData.append('mediaFiles', media.file, media.file.name);
      }
    });

    let valid: boolean = true;

    // Validate attributes
    if (!this.nom) {
      this.errorMessages['nom'] = 'Recipe name is required.';
      valid = false;
    } else this.clearErrorMessages(0);
    if (!this.description) {
      this.errorMessages['description'] = 'Description is required.';
      valid = false;
    } else this.clearErrorMessages(1);
    if (!this.origine) {
      this.errorMessages['origine'] = 'Origin is required.';
      valid = false;
    } else this.clearErrorMessages(2);
    if (this.dureePreparation === undefined) {
      this.errorMessages['dureePreparation'] =
        'Preparation duration is required.';
      valid = false;
    } else this.clearErrorMessages(3);
    if (this.dureeCuisson === undefined) {
      this.errorMessages['dureeCuisson'] = 'Cooking duration is required.';
      valid = false;
    } else this.clearErrorMessages(4);
    if (this.nbrPersonnes === undefined) {
      this.errorMessages['nbrPersonnes'] = 'Number of persons is required.';
      valid = false;
    } else this.clearErrorMessages(5);
    if (this.ingredients.length === 0) {
      this.errorMessages['ingredients'] =
        'At least one ingrediant is required.';
      valid = false;
    } else this.clearErrorMessages(6);
    if (this.quantites.length === 0) {
      this.errorMessages['quantites'] = 'At least one quantity is required.';
      valid = false;
    } else this.clearErrorMessages(7);
    for (let ing of this.ingredients) {
      if (ing.nom === undefined) {
        this.errorMessages['ingredients'] = "Ingredients can't be empty.";
        valid = false;
        break;
      } else this.clearErrorMessages(6);
    }
    for (let quan of this.quantites) {
      if (quan.nombre === undefined || quan.uniteDeMesure === undefined) {
        this.errorMessages['quantites'] = 'Qantity is invalid.';
        valid = false;
        break;
      } else this.clearErrorMessages(7);
    }
    if (this.etapes.length === 0) {
      this.errorMessages['etapes'] = 'At least one step is required.';
      valid = false;
    } else this.clearErrorMessages(8);
    for (let etape of this.etapes) {
      if (etape.description === undefined || etape.duree === undefined) {
        this.errorMessages['etapes'] = 'Steps are invalid.';
        valid = false;
        break;
      } else this.clearErrorMessages(8);
    }
    if (this.medias.length === 0) {
      this.errorMessages['medias'] = 'At least one media is required.';
      valid = false;
    } else this.clearErrorMessages(9);
    for (let med of this.medias) {
      if (med.url === undefined) {
        this.errorMessages['medias'] = "Medias can't be empty.";
        valid = false;
        break;
      } else this.clearErrorMessages(9);
    }
    if (this.categories.length === 0) {
      this.errorMessages['categories'] = 'At least one categorie is required.';
      valid = false;
    } else this.clearErrorMessages(10);
    for (let cat of this.categories) {
      if (cat.nomCat === undefined) {
        this.errorMessages['categories'] = "Categories can't be empty.";
        valid = false;
        break;
      } else this.clearErrorMessages(10);
    }
    console.log(this.errorMessages);

    if (valid) {
      let newRecetteData: Recette = {
        nom: this.nom,
        description: this.description,
        origine: this.origine,
        dureePreparation: this.dureePreparation,
        dureeCuisson: this.dureeCuisson,
        nbrPersonnes: this.nbrPersonnes,
        utilisateurCreateur: this.utilisateurCreateur,
        quantites: this.quantites,
        ingredients: this.ingredients,
        categories: this.categories,
        motCles: this.motCles,
        etapes: this.etapes,
        medias: this.medias,
      };
      if (this.id === undefined) {
        this.recetteService.createRecette(newRecetteData).subscribe(
          (response) => {
            console.log('New recette created successfully!', response);
            alert('New recette created successfully!' + response);
            this.router.navigate(['recettes']);
            // Handle success, e.g., show a success message or navigate to another page
          },
          (error) => {
            console.error('Error creating recette:', error);
            alert('Error creating recette:' + error);
            // Handle error, e.g., show an error message
          }
        );

        //else here we're talking about updating the recette
      } else {
        newRecetteData.id = this.id;

        this.recetteService.updateRecette(this.id, newRecetteData).subscribe(
          () => {
            console.log('Recette updated successfully.');
            console.log(newRecetteData);
            alert('Recette updated successfully');
            this.router.navigate(['recettes']);
          },
          (error) => {
            console.log(error);
            alert('Error updating recette');
          }
        );
      }
    }
  }

  clearErrorMessages(index: number): void {
    switch (index) {
      case 0:
        this.errorMessages['nom'] = '';
        break;
      case 1:
        this.errorMessages['origine'] = '';
        break;
      case 2:
        this.errorMessages['description'] = '';
        break;
      case 3:
        this.errorMessages['dureePreparation'] = '';
        break;
      case 4:
        this.errorMessages['dureeCuisson'] = '';
        break;
      case 5:
        this.errorMessages['nbrPersonnes'] = '';
        break;
      case 6:
        this.errorMessages['ingredients'] = '';
        break;
      case 7:
        this.errorMessages['quantites'] = '';
        break;
      case 8:
        this.errorMessages['etapes'] = '';
        break;
      case 9:
        this.errorMessages['medias'] = '';
        break;
      case 10:
        this.errorMessages['categories'] = '';
        break;
    }
  }

  addIngredient(): void {
    const newIngredient: Ingredient = new Ingredient();
    this.ingredients.push(newIngredient);

    const newQuantite: Quantite = {};
    this.quantites.push(newQuantite);
  }

  removeIngredient(index: number): void {
    this.ingredients.splice(index, 1);
    this.quantites.splice(index, 1);
  }
  handleInputIng(index: number) {
    const IngredientInput = this.ingredients[index].nom;
    const matchedIngredient = this.ingrediantss.find(
      (ing: { nom: string | undefined }) => ing.nom === IngredientInput
    );

    if (matchedIngredient) {
      this.ingredients[index] = matchedIngredient;
    }
  }
  getSuggestedIngredients(input: any): Ingredient[] {
    return this.ingrediantss.filter((ing: { nom: string }) =>
      ing.nom.toLowerCase().includes(input.toLowerCase())
    );
  }

  addCategorie(): void {
    const newCategorie: Categorie = new Categorie();
    this.categories.push(newCategorie);
  }

  removeCategorie(index: number): void {
    this.categories.splice(index, 1);
  }

  onMediaFileSelectedEtape(event: Event, index: number): void {
    const inputElement = event.target as HTMLInputElement;
    if (inputElement.files && inputElement.files.length > 0) {
      const file = inputElement.files[0];
      const reader = new FileReader();

      // Read the file as a Data URL (Base64)
      reader.readAsDataURL(file);

      // Once the file is read, check the length of the Base64 URL
      reader.onload = () => {
        const base64Url = reader.result as string;
        const maxTextSize = 65535; // This is the maximum size for TEXT type in MySQL

        if (base64Url.length > maxTextSize) {
          // Display a message to the user about the size limit
          this.errorEtape[index] = 'Image size is too big.';
          this.messageEtape[index] = '';
          return;
        } else {
          this.errorEtape[index] = '';
          this.messageEtape[index] = '';
        }

        // If the URL is within the limit, store the URL and file in the media object
        this.etapes[index] = {
          media: base64Url,
          file: file,
          description : this.etapes[index].description,
          duree : this.etapes[index].duree
        };
      };
    }
  }

  addEtape(): void {
    const newStep: Etape = {};
    this.etapes.push(newStep);
    this.messageEtape.push('Upload image (Optional)');
  }

  removeEtape(index: number): void {
    this.etapes.splice(index, 1);
    this.errorEtape.splice(index, 1);
    this.messageEtape.splice(index, 1);
  }

  addMotCle(): void {
    const newMotCle: MotCle = new MotCle();
    this.motCles.push(newMotCle);
    console.log(this.motCles);
  }

  removeMotCle(index: number): void {
    this.motCles.splice(index, 1);
  }
  //handleInput method to ensure if the input was selected from the datalist or was entered by the user
  handleInput(index: number) {
    const motCleInput = this.motCles[index].mot;
    const matchedMotCle = this.motCless.find(
      (mc: { mot: string | undefined }) => mc.mot === motCleInput
    );

    if (matchedMotCle) {
      this.motCles[index] = matchedMotCle;
    }
  }

  onMediaFileSelected(event: Event, index: number): void {
    const inputElement = event.target as HTMLInputElement;
    if (inputElement.files && inputElement.files.length > 0) {
      const file = inputElement.files[0];
      const reader = new FileReader();

      // Read the file as a Data URL (Base64)
      reader.readAsDataURL(file);

      // Once the file is read, check the length of the Base64 URL
      reader.onload = () => {
        const base64Url = reader.result as string;
        const maxTextSize = 65535; // This is the maximum size for TEXT type in MySQL

        if (base64Url.length > maxTextSize) {
          // Display a message to the user about the size limit
          this.errorMedia[index] = 'Image is too big.';
          this.messageMedia[index] = '';
          return;
        } else {
          this.errorMedia[index] = '';
          this.messageMedia[index] = '';
        }

        // If the URL is within the limit, store the URL and file in the media object
        this.medias[index] = {
          url: base64Url,
          file: file,
        };
      };
    }
  }

  addMedia(): void {
    const newMedia: Media = {};
    this.medias.push(newMedia);
    this.messageMedia.push('Upload Image');
  }

  removeMedia(index: number): void {
    this.medias.splice(index, 1);
    this.errorMedia.splice(index, 1);
    this.messageMedia.splice(index, 1);
  }

  cancel(): void {
    this.router.navigate(['/recettes']);
  }

  //Animating and styles --->
  onAddButtonClick(): void {
    // Add a class to the button when clicked to trigger the animation
    const addButton = document.querySelector('.addButt');
    if (addButton) {
      addButton.classList.add('clicked');

      // Remove the class after a short delay to reset the animation
      setTimeout(() => {
        addButton.classList.remove('clicked');
      }, 300); // Change this value to adjust the duration of the animation
    }
  }
}
<div class="mainDiv">
  <div class="myContainer">
    <header class="headTitle">
      <h1
        style="
          text-align: center;
          color: white;
          margin-top: 0px;
          padding-top: 5px;
          font-family: Georgia, 'Times New Roman', Times, serif;
        "
      >
        <div *ngIf="id">Update recipe</div>
        <div *ngIf="!id">Create New Recipe</div>
      </h1>
    </header>
    <form (ngSubmit)="createRecette()">
      <div class="form-group">
        <label for="nom">Recipe Name:</label>
        <input
          type="text"
          id="nom"
          name="nom"
          required
          ngModel
          [(ngModel)]="nom"
          #nomInput="ngModel"
        />
        <div
          class="alert alert-danger"
          *ngIf="nomInput.touched && !nomInput.valid"
        >
          Recipe name is required
        </div>
        <div
          *ngIf="errorMessages['nom'] && !(nomInput.touched && !nomInput.valid)"
          class="alert alert-danger"
        >
          {{ errorMessages["nom"] }}
        </div>
      </div>

      <div class="form-group">
        <label for="description">Description:</label>
        <textarea
          id="description"
          name="description"
          required
          ngModel
          [(ngModel)]="description"
          #descInput="ngModel"
        ></textarea>
        <div
          class="alert alert-danger"
          *ngIf="descInput.touched && !descInput.valid"
        >
          Description is required
        </div>
        <div
          *ngIf="
            errorMessages['description'] &&
            !(descInput.touched && !descInput.valid)
          "
          class="alert alert-danger"
        >
          {{ errorMessages["description"] }}
        </div>
      </div>

      <div class="form-group">
        <label for="origine">Origin:</label>
        <input
          type="text"
          id="origine"
          name="origine"
          required
          ngModel
          [(ngModel)]="origine"
          #origineInput="ngModel"
        />
        <div
          class="alert alert-danger"
          *ngIf="origineInput.touched && !origineInput.valid"
        >
          Origine is required
        </div>
        <div
          *ngIf="
            errorMessages['origine'] &&
            !(origineInput.touched && !origineInput.valid)
          "
          class="alert alert-danger"
        >
          {{ errorMessages["origine"] }}
        </div>
      </div>

      <div class="form-group" style="display: flex">
        <input
          type="number"
          id="dureePreparation"
          name="dureePreparation"
          class="threeDiv"
          required
          min="0"
          ngModel
          placeholder="Preparation Time (min):"
          [(ngModel)]="dureePreparation"
          #durPrepaInput="ngModel"
        />
        <!-- <div
          class="alert alert-danger"
          *ngIf="durPrepaInput.touched && !durPrepaInput.valid"
        >
          ?
        </div> -->
        <input
          type="number"
          id="dureeCuisson"
          name="dureeCuisson"
          class="threeDiv"
          required
          min="0"
          ngModel
          placeholder="Cooking Time (min)"
          [(ngModel)]="dureeCuisson"
          #durCuisInput="ngModel"
        />
        <!-- <div
          class="alert alert-danger"
          *ngIf="durCuisInput.touched && !durCuisInput.valid"
        >
          Cooking time is required
        </div> -->
        <input
          type="number"
          id="nbrPersonnes"
          name="nbrPersonnes"
          class="threeDiv"
          required
          min="1"
          ngModel
          placeholder="Number of Servings"
          [(ngModel)]="nbrPersonnes"
          #nbrPersonneInput="ngModel"
        />
        <!-- <div
          class="alert alert-danger"
          *ngIf="nbrPersonneInput.touched && !nbrPersonneInput.valid"
        >
          Number of personne is invalid
        </div> -->
      </div>
      <div
        *ngIf="
          errorMessages['dureePreparation'] ||
          errorMessages['dureeCuisson'] ||
          errorMessages['nbrPersonnes']
        "
        class="alert alert-danger"
      >
        <div>{{ errorMessages["dureePreparation"] }}</div>
        <div>{{ errorMessages["dureeCuisson"] }}</div>
        <div>{{ errorMessages["nbrPersonnes"] }}</div>
      </div>

      <!-- Dynamically add ingredients -->
      <div class="form-group myForm-groupe">
        <div
          class="child-form"
          *ngFor="let ingredient of ingredients; let i = index"
        >
          <strong>Ingredient {{ i + 1 }}</strong>
          <!-- <select
            [(ngModel)]="ingredients[i]"
            name="ingredients{{ i }}"
            class="custom-select"
            aria-label="Default select example"
            [ngModelOptions]="{ standalone: true }"
          >
            <option *ngFor="let ing of ingrediantss" [ngValue]="ing">
              {{ ing.nom }}
            </option>
          </select> -->

          <input
            type="text"
            id="inputLis"
            list="options"
            required
            [(ngModel)]="ingredients[i].nom"
            name="ingredient{{ i }}"
            #ingredientInput="ngModel"
            (input)="handleInputIng(i)"
          />
          <datalist id="options">
            <option *ngFor="let ing of ingrediantss" [value]="ing.nom">
              {{ ing.nom }}
            </option>
          </datalist>
          <div
            class="alert alert-danger"
            *ngIf="ingredientInput.touched && !ingredientInput.valid"
          >
            Ingredient is required
          </div>

          <div class="two-col">
            <input
              type="number"
              style="background-color: white"
              [(ngModel)]="quantites[i].nombre"
              required
              name="quantiteNombre{{ i }}"
              #quantiteInputNombre="ngModel"
              placeholder="nombre de quantite"
            />
            <input
              type="text"
              [(ngModel)]="quantites[i].uniteDeMesure"
              required
              name="quantiteUnite{{ i }}"
              #quantiteInputUnite="ngModel"
              placeholder="unite de mesure"
            />
          </div>
          <div
            class="alert alert-danger"
            *ngIf="
              (quantiteInputNombre.touched && !quantiteInputNombre.valid) ||
              (quantiteInputUnite.touched && !quantiteInputUnite.valid)
            "
          >
            <div
              *ngIf="quantiteInputNombre.touched && !quantiteInputNombre.valid"
            >
              Nombre is required
            </div>
            <div
              *ngIf="quantiteInputUnite.touched && !quantiteInputUnite.valid"
            >
              Unite is required
            </div>
          </div>

          <button
            type="button"
            class="myButton removeButt"
            (click)="removeIngredient(i)"
          >
            Remove
          </button>
        </div>
        <button
          type="button"
          class="myButton addButt"
          (click)="addIngredient()"
          (click)="onAddButtonClick()"
        >
          Add Ingredient
        </button>
        <div
          *ngIf="errorMessages['quantites'] || errorMessages['ingredients']"
          class="alert alert-danger"
          style="margin-top: 10px"
        >
          <div>{{ errorMessages["ingredients"] }}</div>
          <div>{{ errorMessages["quantites"] }}</div>
        </div>
      </div>

      <!-- Dynamically add steps (etapes) -->
      <div class="form-group myForm-groupe">
        <div class="child-form" *ngFor="let etape of etapes; let i = index">
          <strong>Step {{ i + 1 }}</strong>

          <div class="two-col">
            <textarea
              [(ngModel)]="etape.description"
              name="etapeDesc{{ i }}"
              required
              maxlength="250"
              #etapeDesc="ngModel"
              placeholder="description de l'etape"
            ></textarea>

            <span class="dragBox">
              <input
                [(ngModel)]="etape.media"
                type="file"
                id="inputList"
                (change)="onMediaFileSelectedEtape($event, i)"
                accept="image/jpeg, image/png, image/gif"
              />
              {{ messageEtape[i] }}
              <img
                *ngIf="etape.media"
                [src]="etape.media"
                alt="Image"
                class="image-preview"
              />
            </span>
          </div>

          <input
            type="number"
            [(ngModel)]="etape.duree"
            name="etapeDuree{{ i }}"
            required
            min="0"
            #etapeDuree="ngModel"
            placeholder="duree en minutes"
          />

          <div
            *ngIf="
              (etapeDesc.invalid && etapeDesc.touched) ||
              (etapeDuree.invalid && etapeDuree.touched) ||
              errorEtape[i]
            "
            class="alert alert-danger"
          >
            <div>{{ errorEtape[i] }}</div>
            <div *ngIf="etapeDesc.invalid && etapeDesc.touched">
              Description is required
            </div>
            <div *ngIf="etapeDuree.invalid && etapeDuree.touched">
              Duree is required
            </div>
          </div>

          <button
            type="button"
            class="myButton removeButt"
            (click)="removeEtape(i)"
          >
            Remove
          </button>
        </div>
        <button
          type="button"
          class="myButton addButt"
          (click)="addEtape()"
          (click)="onAddButtonClick()"
        >
          Add Step
        </button>
        <div
          *ngIf="errorMessages['etapes']"
          class="alert alert-danger"
          style="margin-top: 10px"
        >
          <div>{{ errorMessages["etapes"] }}</div>
        </div>
      </div>

      <!-- Dynamically add categories -->
      <div class="form-group myForm-groupe">
        <div class="child-form" *ngFor="let cat of categories; let i = index">
          <p>Categorie {{ i + 1 }}</p>
          <select
            [(ngModel)]="categories[i]"
            class="form-select"
            aria-label="Default select example"
            name="categorie"
            required
            #categoriSelect="ngModel"
          >
            <option [ngValue]="categories[i]">
              {{ categories[i].nomCat }}
            </option>
            <option *ngFor="let categorie of categoriess" [ngValue]="categorie">
              {{ categorie.nomCat }}
            </option>
          </select>
          <div
            class="alert alert-danger"
            *ngIf="categoriSelect.invalid && categoriSelect.touched"
          >
            Categorie is required
          </div>
          <button
            type="button"
            class="myButton removeButt"
            (click)="removeCategorie(i)"
          >
            Remove
          </button>
        </div>
        <button
          type="button"
          class="myButton addButt"
          (click)="addCategorie()"
          (click)="onAddButtonClick()"
        >
          Add Categorie
        </button>
        <div
          *ngIf="errorMessages['categories']"
          class="alert alert-danger"
          style="margin-top: 10px"
        >
          <div>{{ errorMessages["categories"] }}</div>
        </div>
      </div>

      <!-- Media upload -->
      <div class="form-group myForm-groupe">
        <div class="media-container-row">
          <ng-container
            class="child-form"
            *ngFor="let media of medias; let i = index"
          >
            <div class="media-container">
              <span class="dragBox">
                <input
                  type="file"
                  id="inputList"
                  (change)="onMediaFileSelected($event, i)"
                  accept="image/jpeg, image/png, image/gif"
                  name="media"
                  required
                  [ngModel]="media.url"
                  #mediaInput="ngModel"
                />

                {{ messageMedia[i] }}
                <img
                  *ngIf="media.url"
                  [src]="media.url"
                  alt="Image"
                  class="image-preview"
                />
              </span>
              <div
                class="alert alert-danger"
                *ngIf="
                  (!mediaInput.valid && mediaInput.touched) || errorMedia[i]
                "
              >
                <div>{{ errorMedia[i] }}</div>
                <div *ngIf="!mediaInput.valid && mediaInput.touched">
                  Media for recipe is required
                </div>
              </div>
              <span style="color: red"> </span>
              <button
                type="button"
                class="myButton removeButt"
                (click)="removeMedia(i)"
              >
                Remove
              </button>
            </div>
          </ng-container>
        </div>

        <button type="button" class="myButton addButt" (click)="addMedia()">
          Add Media
        </button>
        <div
          *ngIf="errorMessages['medias']"
          class="alert alert-danger"
          style="margin-top: 10px"
        >
          <div>{{ errorMessages["medias"] }}</div>
        </div>
      </div>

      <div class="form-group myForm-groupe">
        <div class="input-container">
          <div class="child-form" *ngFor="let mot of motCles; let i = index">
            <!-- <input
              type="text"
              id="inputLis"
              list="options"
              [(ngModel)]="motCles[i].mot"
              [ngModelOptions]="{ standalone: true }"
              (input)="handleInput(i)"
            />
            <datalist id="optionss">
              <option *ngFor="let mc of motCless" [value]="mc.mot"></option>
            </datalist> -->
            <select
              [(ngModel)]="motCles[i]"
              class="form-select"
              aria-label="Default select example"
              [ngModelOptions]="{ standalone: true }"
            >
              <option [ngValue]="motCles[i]">
                {{ motCles[i].mot }}
              </option>
              <option *ngFor="let motCle of motCless" [ngValue]="motCle">
                {{ motCle.mot }}
              </option>
            </select>
            <button
              type="button"
              class="myButton removeButt"
              (click)="removeMotCle(i)"
            >
              Remove
            </button>
          </div>
        </div>
        <button type="button" class="myButton addButt" (click)="addMotCle()">
          Add Tags
        </button>
      </div>

      <br />
      <hr />
      <br />
      <div class="form-group bossDiv">
        <button type="submit" class="btn bossBtn">
          <div *ngIf="id">Update recipe</div>
          <div *ngIf="!id">Create Recipe</div>
        </button>
        <button type="button" class="btn bossBtn" (click)="cancel()">
          Cancel
        </button>
      </div>
    </form>
  </div>
</div>
