import { Component, OnInit } from '@angular/core';
import { Router, NavigationEnd } from '@angular/router';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {
  currentUrl= '';

  constructor(private router: Router) { }

  viewRecettes(): void {
    this.router.navigate(['recettes']);
  }

  addRecette(): void {
    this.router.navigate(['recettes/add']);
  }

  login(): void {
    this.router.navigate(['login']);
  }

  status = false;

  addToggle() {
    this.status = !this.status;
  }

  ngOnInit() {
    // Subscribe to router events to track the current URL
    this.router.events.subscribe((event) => {
      if (event instanceof NavigationEnd) {
        // Update the currentUrl variable with the current URL
        this.currentUrl = event.url;
      }
    });
  }

  isAddRecettesPage() {
    return this.currentUrl === '/recettes/add';
  }

  isUpdateRecettePage() {
    return this.currentUrl.includes('/recettes/update/');
  }
}
<!--
  - #HEADER
-->

<header class="header" [style.position]="isAddRecettesPage() || isUpdateRecettePage() ? 'static' : 'absolute'">

  <div class="container">

    <h1>
      <a href="#" class="logo">Foodie<span class="span">.</span></a>
    </h1>

    <nav class="navbar" data-navbar>
      <ul class="navbar-list header-links">

        <li class="nav-item">
          <a href="#home" class="navbar-link " data-nav-link>Home</a>
        </li>

        <li class="nav-item">
          <a href="#about" class="navbar-link" data-nav-link>About Us</a>
        </li>

        <li class="nav-item">
          <a href="#" routerLink="/recettes" class="navbar-link" data-nav-link>Recipes</a>
        </li>

        <li class="nav-item">
          <a href="#" routerLink="/recettes/add" class="navbar-link" data-nav-link>Create Recipes</a>
        </li>

        <li class="nav-item">
          <a href="#" routerLink="/categories" class="navbar-link" data-nav-link>Categories</a>
        </li>

      </ul>
    </nav>

    <div class="header-btn-group">
      <button class="btn btn-hover login-btn" routerLink="/login">Login</button>

      <button class="nav-toggle-btn" aria-label="Toggle Menu" data-menu-toggle-btn (click)="addToggle()">
        <span class="line top"></span>
        <span class="line middle"></span>
        <span class="line bottom"></span>
      </button>
    </div>

  </div>
</header>
