import { PersonneService } from './../services/personne.service';
import { AuthService } from './../services/auth.service';
import { Component, OnInit } from '@angular/core';
import { Router, NavigationEnd } from '@angular/router';
import { Personne } from '../models/personne';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent implements OnInit {
  currentUrl= '';

  constructor(private router: Router, private authService: AuthService, private personneServ: PersonneService) { }

  ngOnInit() {
    // Subscribe to router events to track the current URL
    this.router.events.subscribe((event) => {
      if (event instanceof NavigationEnd) {
        // Update the currentUrl variable with the current URL
        this.currentUrl = event.url;
      }
    });
  }
  viewRecettes(): void {
    this.router.navigate(['recettes']);
  }

  addRecette(): void {
    this.router.navigate(['recettes/add']);
  }

  login(): void {
    this.router.navigate(['login']);
  }

  status = false;

  addToggle() {
    this.status = !this.status;
  }



  //Flexing of the header during entering the recette page
  isAddRecettesPage() {
    return this.currentUrl === '/recettes_add';
  }

  isUpdateRecettePage() {
    return this.currentUrl.includes('/recettes/update/');
  }

  isAuthenticated(): boolean{
    return this.authService.isAuthenticated();
  }

  logout() {
    this.authService.logout().subscribe(
      (data) => {
        localStorage.removeItem('access_token');
        localStorage.removeItem('refresh_token');
        this.router.navigate(['/login']);
      },
      (error) => {
        console.error('Logout error : '+error);
      }
    );
  }
}
<!--
  - #HEADER
-->


<header
  class="header"
  [style.position]="
    isAddRecettesPage() || isUpdateRecettePage() ? 'static' : 'absolute'
  "
>
  <div class="container">
    <h1>
      <a href="#" routerLink="/home" class="logo">Foodie<span class="span">.</span></a>
    </h1>

    <nav class="navbar" data-navbar>
      <ul class="navbar-list header-links">
        <li class="nav-item">
          <a href="#home" class="navbar-link" data-nav-link>Home</a>
        </li>

        <li class="nav-item">
          <a href="#about" class="navbar-link" data-nav-link>About Us</a>
        </li>

        <li class="nav-item">
          <a href="#" routerLink="/recettes" class="navbar-link" data-nav-link
            >Recipes</a
          >
        </li>

        <li class="nav-item">
          <a
            href="#"
            routerLink="/recettes_add"
            class="navbar-link"
            data-nav-link
            >Create Recipes</a
          >
        </li>

        <li class="nav-item">
          <a href="#" routerLink="/categories" class="navbar-link" data-nav-link
            >Categories</a
          >
        </li>
        <li class="nav-item">
          <a href="#" routerLink="/ingredients" class="navbar-link" data-nav-link
            >Ingredients</a
          >
        </li>
      </ul>
    </nav>

    <!-- TODO Fix button logout not working here -->
    <div class="header-btn-group">
      <button class="btn btn-hover login-btn" *ngIf="!isAuthenticated()" routerLink="/login">
        <i class="fa-solid fa-user" style="padding-inline: 5px"></i>Login
      </button>

      <button class="btn btn-hover login-btn" *ngIf="isAuthenticated()" (click)="logout()">
        <i class="fa-solid fa-arrow-right-from-bracket" style="padding-inline: 5px"></i>Logout
      </button>


      <button
        class="nav-toggle-btn"
        aria-label="Toggle Menu"
        data-menu-toggle-btn
        (click)="addToggle()"
      >
        <span class="line top"></span>
        <span class="line middle"></span>
        <span class="line bottom"></span>
      </button>
    </div>
  </div>
</header>
